package com.class8;

public class Task {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i = 1; i<=100; i++) {
			System.out.print(i+" ");
		}
		
		for (int i = 100; i>=1; i--) {
			System.out.println(i);
		}
		for (int i = 20; i>=2; i-=2) {
			System.out.println(i);
		}
		for (int i = 21; i<=50; i++) {
			if (i % 2 !=0);
			System.out.println(i);
		}
		
	}

}
